    users = content.split('\n');
    result = "";
    for(user_index in users) {
        if(users[user_index]) {
            infos = users[user_index].split(':');
            result += '<li><span class="avatar" />' + infos[0] + '<br/>' + infos[1] + '</li>';
        }
    };
